var addRemoveFriendFunc=function(e){e.click((function(t){t.preventDefault();let n=e.attr("data-friend-id");$.ajax({method:"get",url:e.attr("href"),success:function(e){new Noty({theme:"relax",layout:"topRight",text:`${e.message}`,type:"success",timeout:1500}).show()},error:function(e){console.log(e.responseText)}}),$(`#${n}`).remove()}))};$(".remove-friend-button").each((function(){addRemoveFriendFunc($(this))}));