$("#toggle-friend-button").click((function(t){t.preventDefault();let e=$(this).attr("data-user-id");$.ajax({method:"get",url:$(this).attr("href"),success:function(t){new Noty({theme:"relax",layout:"topRight",text:`${t.message}`,type:"success",timeout:1500}).show()},error:function(t){console.log(t.responseText)}}),"Add Friend"==$(this).text()?$(this).attr("href",`/users/friendships/destroy/?id=${e}`).text("Remove Friend"):$(this).attr("href",`/users/friendships/create/?id=${e}`).text("Add Friend")}));